'use client'
import Image from 'next/image'
import { useEffect, useState } from 'react'

const mockData = {
  frames: [
    {
      image:
        'data:image/png;base64,',
      duration: 100,
    },
    {
      image:
        'data:image/png;base64,',
      duration: 100,
    },
    {
      image:
        'data:image/png;base64,',
      duration: 100,
    },
    {
      image:
        'data:image/png;base64,',
      duration: 100,
    },
    {
      image:
        'data:image/png;base64,',
      duration: 100,
    },
    {
      image:
        'data:image/png;base64,',
      duration: 100,
    },
    {
      image:
        'data:image/png;base64,',
      duration: 100,
    },
    {
      image:
        'data:image/png;base64,',
      duration: 100,
    },
  ],
}

export const Outfit = () => {
  const [currentFrame, setCurrentFrame] = useState(0)

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentFrame((currentFrame) => {
        return (currentFrame + 1) % mockData.frames.length
      })
    }, mockData.frames[currentFrame].duration)

    return () => clearInterval(interval)
  }, [currentFrame])

  return (
    <div className="flex flex-col items-center justify-center">
      <Image
        src={mockData.frames[currentFrame].image}
        alt="outfit"
        width={64}
        height={64}
      />
      <span className="text-3xl text-white sm:text-sm shadow-primary font-fondamento">
        {currentFrame}
      </span>
    </div>
  )
}
